    if (typeof raw === 'string') {
      try {
        const parsed = JSON.parse(raw);
        if (parsed?.message) {
          if (parsed.message.includes('ƒë√£ ƒë∆∞·ª£c ƒëƒÉng k√Ω') && selectedEmployee) {
            const match = parsed.message.match(/nh√¢n vi√™n: (.+)/);
