import React from 'react';
import { Form, Input, DatePicker, TimePicker, Select, Button, Card, message } from 'antd';
import { nghiPhepService } from '../../services/nghiPhepService';

const DonNghiPhepForm = ({ onSuccess }) => {
  const [form] = Form.useForm();

  const onFinish = async (values) => {
    const res = await nghiPhepService.create({
      ...values,
      ngay_nghi: values.ngay_nghi.format('YYYY-MM-DD'),
      thoi_gian_bat_dau: values.thoi_gian_bat_dau ? values.thoi_gian_bat_dau.format('HH:mm') : undefined,
      thoi_gian_ket_thuc: values.thoi_gian_ket_thuc ? values.thoi_gian_ket_thuc.format('HH:mm') : undefined
    });
    if (res.success) {
      message.success('Gửi đơn thành công!');
      form.resetFields();
      onSuccess && onSuccess();
    } else {
      message.error('Gửi đơn thất bại!');
    }
  };

  return (
    <Card title="Gửi đơn nghỉ phép" style={{ maxWidth: 500, margin: '32px auto' }}>
      <Form form={form} layout="vertical" onFinish={onFinish}>
        <Form.Item name="nhan_vien_id" label="ID nhân viên" rules={[{ required: true }]}> <Input /> </Form.Item>
        <Form.Item name="ngay_nghi" label="Ngày nghỉ" rules={[{ required: true }]}> <DatePicker style={{ width: '100%' }} /> </Form.Item>
        <Form.Item name="loai_nghi" label="Loại nghỉ" initialValue="ca_ngay"> <Select> <Select.Option value="ca_ngay">Cả ngày</Select.Option> <Select.Option value="nua_ngay">Nửa ngày</Select.Option> <Select.Option value="theo_gio">Theo giờ</Select.Option> </Select> </Form.Item>
        <Form.Item name="thoi_gian_bat_dau" label="Thời gian bắt đầu"> <TimePicker format="HH:mm" /> </Form.Item>
        <Form.Item name="thoi_gian_ket_thuc" label="Thời gian kết thúc"> <TimePicker format="HH:mm" /> </Form.Item>
        <Form.Item name="ly_do" label="Lý do"> <Input /> </Form.Item>
        <Button type="primary" htmlType="submit">Gửi đơn</Button>
      </Form>
    </Card>
  );
};

export default DonNghiPhepForm;
