import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AiDashboard = () => {
  const [stats, setStats] = useState(null);
  const [predictions, setPredictions] = useState([]);
  const [loading, setLoading] = useState(false);

  const [employeePrediction, setEmployeePrediction] = useState(null);
  const [trends, setTrends] = useState(null);
  const [anomalies, setAnomalies] = useState([]);
  const [modelVersion, setModelVersion] = useState(1);
  const [autoUpdate, setAutoUpdate] = useState(false);

  useEffect(() => {
    loadAiStats();
    loadBatchPredictions();
    loadTrends();
    
    // Auto-refresh every 5 minutes if enabled
    if (autoUpdate) {
      const interval = setInterval(() => {
        loadAiStats();
        loadBatchPredictions();
        loadTrends();
      }, 300000); // 5 minutes
      
      return () => clearInterval(interval);
    }
  }, [autoUpdate]);

  const loadAiStats = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:8000/api/ai-analysis/stats');
      if (response.data.success) {
        setStats(response.data.data);
        setModelVersion(response.data.data.model_info?.version || 1);
      }
    } catch (error) {
      console.error('L·ªói khi t·∫£i th·ªëng k√™ AI:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadBatchPredictions = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:8000/api/ai-analysis/predict-batch-attrition');
      if (response.data.success) {
        setPredictions(response.data.data.predictions);
      }
    } catch (error) {
      console.error('L·ªói khi t·∫£i d·ª± ƒëo√°n h√†ng lo·∫°t:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadTrends = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/ai-analysis/analyze-trends');
      if (response.data.success) {
        setTrends(response.data.data);
        setAnomalies(response.data.data.anomalies || []);
      }
    } catch (error) {
      console.error('L·ªói khi t·∫£i xu h∆∞·ªõng:', error);
    }
  };

  const updateModel = async () => {
    try {
      setLoading(true);
      const response = await axios.post('http://localhost:8000/api/ai-analysis/update-model');
      if (response.data.success) {
        alert('‚úÖ C·∫≠p nh·∫≠t model AI th√†nh c√¥ng!');
        loadAiStats(); // Reload stats
      } else {
        alert('‚ùå L·ªói c·∫≠p nh·∫≠t model: ' + response.data.message);
      }
    } catch (error) {
      console.error('L·ªói c·∫≠p nh·∫≠t model:', error);
      alert('‚ùå L·ªói c·∫≠p nh·∫≠t model AI');
    } finally {
      setLoading(false);
    }
  };

  const predictEmployee = async (employeeId) => {
    try {
      const response = await axios.post('http://localhost:8000/api/ai-analysis/predict-attrition', {
        nhan_vien_id: employeeId
      });
      if (response.data.success) {
        setEmployeePrediction(response.data.data);
      }
    } catch (error) {
      console.error('L·ªói khi d·ª± ƒëo√°n nh√¢n vi√™n:', error);
    }
  };

  const getRiskColor = (riskLevel) => {
    switch (riskLevel) {
      case 'Cao': return '#dc3545';
      case 'Trung b√¨nh': return '#ffc107';
      case 'Th·∫•p': return '#28a745';
      default: return '#6c757d';
    }
  };

  const getRiskIcon = (riskLevel) => {
    switch (riskLevel) {
      case 'Cao': return 'üî¥';
      case 'Trung b√¨nh': return 'üü°';
      case 'Th·∫•p': return 'üü¢';
      default: return '‚ö™';
    }
  };

  const getTrendIcon = (direction) => {
    switch (direction) {
      case 'increasing': return 'üìà';
      case 'decreasing': return 'üìâ';
      case 'stable': return '‚û°Ô∏è';
      default: return '‚ùì';
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '1400px', margin: '0 auto' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '30px', color: '#007bff' }}>
        ü§ñ AI Ph√¢n T√≠ch Nh√¢n S·ª± Th√¥ng Minh
      </h2>

      {/* Control Panel */}
      <div style={{
        backgroundColor: '#f8f9fa',
        padding: '15px',
        borderRadius: '10px',
        marginBottom: '20px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        flexWrap: 'wrap',
        gap: '10px'
      }}>
        <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <button
            onClick={loadAiStats}
            disabled={loading}
            style={{
              padding: '8px 16px',
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: loading ? 'not-allowed' : 'pointer'
            }}
          >
            üîÑ Refresh
          </button>
          
          <button
            onClick={updateModel}
            disabled={loading}
            style={{
              padding: '8px 16px',
              backgroundColor: '#28a745',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: loading ? 'not-allowed' : 'pointer'
            }}
          >
            üß† C·∫≠p Nh·∫≠t Model
          </button>
        </div>

        <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <label style={{ display: 'flex', alignItems: 'center', gap: '5px' }}>
            <input
              type="checkbox"
              checked={autoUpdate}
              onChange={(e) => setAutoUpdate(e.target.checked)}
            />
            Auto-refresh (5 ph√∫t)
          </label>
          
          <span style={{ 
            padding: '4px 8px', 
            backgroundColor: '#17a2b8', 
            color: 'white', 
            borderRadius: '4px',
            fontSize: '12px'
          }}>
            Model v{modelVersion}
          </span>
        </div>
      </div>

      {/* Th·ªëng k√™ t·ªïng quan */}
      {stats && (
        <div style={{
          backgroundColor: '#f8f9fa',
          padding: '20px',
          borderRadius: '10px',
          marginBottom: '30px',
          border: '1px solid #dee2e6'
        }}>
          <h3 style={{ color: '#495057', marginBottom: '15px' }}>üìä Th·ªëng K√™ T·ªïng Quan</h3>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '20px' }}>
            <div style={{ textAlign: 'center' }}>
              <h4 style={{ color: '#28a745', margin: '0' }}>{stats.employee_stats.total}</h4>
              <p style={{ margin: '5px 0', color: '#6c757d' }}>T·ªïng nh√¢n vi√™n</p>
            </div>
            <div style={{ textAlign: 'center' }}>
              <h4 style={{ color: '#007bff', margin: '0' }}>{stats.employee_stats.active}</h4>
              <p style={{ margin: '5px 0', color: '#6c757d' }}>ƒêang l√†m vi·ªác</p>
            </div>
            <div style={{ textAlign: 'center' }}>
              <h4 style={{ color: '#dc3545', margin: '0' }}>{stats.employee_stats.left}</h4>
              <p style={{ margin: '5px 0', color: '#6c757d' }}>ƒê√£ ngh·ªâ vi·ªác</p>
            </div>
            <div style={{ textAlign: 'center' }}>
              <h4 style={{ color: '#ffc107', margin: '0' }}>{stats.employee_stats.attrition_rate}%</h4>
              <p style={{ margin: '5px 0', color: '#6c757d' }}>T·ª∑ l·ªá ngh·ªâ vi·ªác</p>
            </div>
          </div>
        </div>
      )}

      {/* Xu h∆∞·ªõng v√† c·∫£nh b√°o */}
      {trends && (
        <div style={{
          backgroundColor: '#fff3cd',
          padding: '20px',
          borderRadius: '10px',
          marginBottom: '30px',
          border: '1px solid #ffeaa7'
        }}>
          <h3 style={{ color: '#856404', marginBottom: '15px' }}>üìà Ph√¢n T√≠ch Xu H∆∞·ªõng</h3>
          
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '20px' }}>
            {/* Xu h∆∞·ªõng ch·∫•m c√¥ng */}
            <div>
              <h4 style={{ color: '#856404', marginBottom: '10px' }}>
                {getTrendIcon(trends.attendance_trends?.trend_direction)} Ch·∫•m C√¥ng
              </h4>
              <p>ƒêi mu·ªôn TB: {trends.attendance_trends?.avg_late_minutes?.toFixed(1)} ph√∫t</p>
              <p>V·ªÅ s·ªõm TB: {trends.attendance_trends?.avg_early_minutes?.toFixed(1)} ph√∫t</p>
              <p>Xu h∆∞·ªõng: {trends.attendance_trends?.trend_direction}</p>
            </div>

            {/* Xu h∆∞·ªõng ngh·ªâ ph√©p */}
            <div>
              <h4 style={{ color: '#856404', marginBottom: '10px' }}>
                {getTrendIcon(trends.leave_trends?.trend_direction)} Ngh·ªâ Ph√©p
              </h4>
              <p>T·ªïng ng√†y ngh·ªâ: {trends.leave_trends?.total_leave_days}</p>
              <p>TB/ng√†y: {trends.leave_trends?.avg_leaves_per_day?.toFixed(1)}</p>
              <p>Xu h∆∞·ªõng: {trends.leave_trends?.trend_direction}</p>
            </div>
          </div>

          {/* C·∫£nh b√°o b·∫•t th∆∞·ªùng */}
          {anomalies.length > 0 && (
            <div style={{ marginTop: '20px' }}>
              <h4 style={{ color: '#dc3545', marginBottom: '10px' }}>‚ö†Ô∏è C·∫£nh B√°o B·∫•t Th∆∞·ªùng</h4>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '10px' }}>
                {anomalies.map((anomaly, index) => (
                  <div key={index} style={{
                    backgroundColor: '#f8d7da',
                    padding: '10px',
                    borderRadius: '5px',
                    border: '1px solid #f5c6cb'
                  }}>
                    <strong>{anomaly.employee_name}</strong>
                    <p style={{ margin: '5px 0', fontSize: '14px' }}>{anomaly.description}</p>
                    <small style={{ color: '#721c24' }}>
                      T·ª∑ l·ªá c≈©: {anomaly.data.previous_rate}% ‚Üí M·ªõi: {anomaly.data.current_rate}%
                    </small>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      )}

      {/* D·ª± ƒëo√°n h√†ng lo·∫°t */}
      <div style={{ marginBottom: '30px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h3 style={{ color: '#495057', margin: '0' }}>üéØ D·ª± ƒêo√°n Nguy C∆° Ngh·ªâ Vi·ªác</h3>
          <button
            onClick={loadBatchPredictions}
            disabled={loading}
            style={{
              padding: '10px 20px',
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: loading ? 'not-allowed' : 'pointer'
            }}
          >
            {loading ? '‚è≥ ƒêang t·∫£i...' : 'üîÑ C·∫≠p nh·∫≠t'}
          </button>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '15px' }}>
          {predictions.map((prediction, index) => (
            <div
              key={index}
              style={{
                backgroundColor: 'white',
                padding: '15px',
                borderRadius: '10px',
                border: `2px solid ${getRiskColor(prediction.risk_level)}`,
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
                cursor: 'pointer',
                transition: 'transform 0.2s'
              }}
              onMouseEnter={(e) => e.target.style.transform = 'scale(1.02)'}
              onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
              onClick={() => predictEmployee(prediction.nhan_vien.id)}
            >
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
                <h4 style={{ margin: '0', color: '#495057' }}>
                  {prediction.nhan_vien.ten}
                </h4>
                <span style={{ fontSize: '24px' }}>
                  {getRiskIcon(prediction.risk_level)}
                </span>
              </div>
              
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <span style={{ 
                  padding: '4px 8px', 
                  backgroundColor: getRiskColor(prediction.risk_level), 
                  color: 'white', 
                  borderRadius: '4px',
                  fontSize: '12px'
                }}>
                  {prediction.risk_level}
                </span>
                <span style={{ color: '#6c757d', fontSize: '14px' }}>
                  {(prediction.probability * 100).toFixed(1)}%
                </span>
              </div>
              
              <p style={{ margin: '5px 0', fontSize: '12px', color: '#6c757d' }}>
                ID: {prediction.nhan_vien.ma_nhan_vien}
              </p>
            </div>
          ))}
        </div>
      </div>

      {/* Chi ti·∫øt d·ª± ƒëo√°n nh√¢n vi√™n */}
      {employeePrediction && (
        <div style={{
          backgroundColor: '#e7f3ff',
          padding: '20px',
          borderRadius: '10px',
          marginBottom: '30px',
          border: '1px solid #b3d9ff'
        }}>
          <h3 style={{ color: '#004085', marginBottom: '15px' }}>
            üìã Chi Ti·∫øt D·ª± ƒêo√°n: {employeePrediction.nhan_vien.ten}
          </h3>
          
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '20px' }}>
            <div>
              <h4 style={{ color: '#004085', marginBottom: '10px' }}>K·∫øt Qu·∫£ D·ª± ƒêo√°n</h4>
              <p><strong>Nguy c∆°:</strong> {employeePrediction.risk_level}</p>
              <p><strong>X√°c su·∫•t:</strong> {(employeePrediction.probability * 100).toFixed(1)}%</p>
              <p><strong>D·ª± ƒëo√°n:</strong> {employeePrediction.prediction === 1 ? 'C√≥ nguy c∆°' : 'Kh√¥ng c√≥ nguy c∆°'}</p>
            </div>
            
            <div>
              <h4 style={{ color: '#004085', marginBottom: '10px' }}>Features</h4>
              <p><strong>S·ªë ng√†y ƒëi l√†m:</strong> {employeePrediction.features.so_ngay_di_lam}</p>
              <p><strong>S·ªë l·∫ßn ƒëi mu·ªôn:</strong> {employeePrediction.features.so_lan_di_muon}</p>
              <p><strong>S·ªë l·∫ßn v·ªÅ s·ªõm:</strong> {employeePrediction.features.so_lan_ve_som}</p>
              <p><strong>Gi·ªù l√†m TB:</strong> {employeePrediction.features.gio_lam_viec_tb}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AiDashboard;
